// Temperature
function cToF(&$weather, $field){if(!isset($weather[$field])) return;$weather[$field] = cToFDirect($weather[$field]);}
function fToC(&$weather, $field){if(!isset($weather[$field])) return;$weather[$field] = fToCDirect($weather[$field]);}
